package com.cms.cmsclient.cmsclient.controller;import com.cms.cmsclient.cmsclient.service.HelloService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.beans.factory.annotation.Value;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RestController;@RestControllerpublic class HelloController {    @Autowired    private HelloService helloService;    @Value("${server.port}")    String port;    @GetMapping("/hello")    public String sayHello(String name){        return  helloService.sayHello(name);    }}