package com.cms.cmsclient.cmsclient.rpc;import com.cms.cmsclient.cmsclient.rpc.impl.HelloHystrix;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestParam;@FeignClient(value = "cms-service",fallback = HelloHystrix.class)public interface GetHello {    @GetMapping(value = "/hello")    String getHello(@RequestParam(value = "name") String name);}