package com.cms.cmssetting.controller;import com.cms.cmssetting.service.HelloService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.beans.factory.annotation.Value;import org.springframework.cloud.context.config.annotation.RefreshScope;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RefreshScopepublic class HelloController {    @Autowired    private HelloService helloService;    @Value("${host}")    String host;    @GetMapping("/hello")    public String sayHello(String name){        return  helloService.sayHello(name)+host;    }}